import { readFile } from "node:fs/promises";
import { join } from "node:path";
import { ImageResponse } from "next/og";
import { header, meta } from "@/config/metadata";

export const contentType = "image/png";
export const alt = `${header.title} - ${meta.shortDescription}`;
export const size = {
 width: 1200,
 height: 630,
};

export default async function Image() {
 const fontBold = await readFile(join(process.cwd(), "public/fonts/geist-mono-900.otf"));
 const fontRegular = await readFile(join(process.cwd(), "public/fonts/geist-mono-400.otf"));

 return new ImageResponse(
  (
   <div
    style={{
     height: "100%",
     width: "100%",
     display: "flex",
     padding: "50px",
     flexDirection: "column",
     justifyContent: "flex-end",
     backgroundColor: "#101110",
    }}
   >
    <div
     style={{
      // prettier-ignore
      backgroundImage: "url(\"data:image/png;base64,\")",
      opacity: 0.3,
      backgroundSize: "1203px 849px",
      backgroundRepeat: "no-repeat",
      backgroundPosition: "45%",
      display: "flex",
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      filter: "hue-rotate(540deg) saturate(5)",
      position: "absolute",
      zIndex: 1,
     }}
    />
    <div
     style={{
      color: "#fff",
      fontFamily: "GeistMono-Black",
      fontSize: 64,
     }}
    >
     {header.title}
    </div>
    <div
     style={{
      color: "rgba(255, 255, 255, 0.5)",
      fontFamily: "GeistMono-Regular",
      fontSize: 32,
      fontWeight: 400,
      marginTop: "15px",
     }}
    >
     {meta.shortDescription}
    </div>
   </div>
  ),
  {
   ...size,
   fonts: [
    {
     name: "GeistMono-Black",
     data: await fontBold,
     style: "normal",
    },
    {
     name: "GeistMono-Regular",
     data: await fontRegular,
     style: "normal",
     weight: 400,
    },
   ],
  }
 );
}
